package com.yaoli.ajob;

import java.util.*;

/**
 * Created by will on 2017/1/15.
 * https://leetcode.com/problems/find-all-anagrams-in-a-string/
 * 使用 排序的方法 块。
 */
public class FindAllAnagramsinaString {
    public static void main(String[] args) {
        Long a = System.currentTimeMillis();
        System.out.println(findAnagrams(null,null));
        Long b = System.currentTimeMillis();
        System.out.println(b - a);
    }

    public static List<Integer> findAnagrams(String s, String p) {
        s = "";
        p = "";

        List<Integer> list = new ArrayList<Integer>();

        Map<Character,Integer> map = new HashMap<Character,Integer>();
        for(char a : p.toCharArray()){
            if(map.get(a) == null){
                map.put(a,1);
            }else{
                map.put(a,map.get(a)+1);
            }
        }


        for(int i = 0; i <= s.length() - p.length() ; i++){

            String sub = s.substring(i,i+p.length());

            for(int j = 0 ; j < p.length(); j++){
                if(map.get(sub.charAt(j)) == null){
                    break;
                }else{
                    if(map.get(sub.charAt(j)) -1 == 0){
                        map.remove(sub.charAt(j));
                    }else{
                        map.put(sub.charAt(j),map.get(sub.charAt(j)) - 1);
                    }

                }
            }

            if(map.size() == 0){
                list.add(i);
            }

            map.clear();

            for(char a : p.toCharArray()){
                if(map.get(a) == null){
                    map.put(a,1);
                }else{
                    map.put(a,map.get(a)+1);
                }
            }
        }

        return list;
    }

    //使用排序 的方法
    public List<Integer> findAnagrams1(String s, String p) {
        List<Integer> list = new ArrayList<Integer>();

        char [] ps = p.toCharArray();
        Arrays.sort(ps);

        for(int i = 0; i <= s.length() - p.length() ; i++){
            boolean flag = true;

            String sub = s.substring(i,i+p.length());

            char[] temp = sub.toCharArray();

            Arrays.sort(temp);

            for(int j = 0 ; j < sub.length(); j++){
                if(temp[j] != ps[j]){
                    flag = false;
                    break;
                }

            }
            if(flag == true){
                list.add(i);
            }
        }

        return list;
    }
}
