package com.yaoli.ajob;
import java.util.Map;
import java.util.HashMap;
/**
 * Created by will on 2017/2/9.
 */
public class _389_FindtheDifference {
    public static void main(String[] args) {
        String a = "";

        String b = "";
        System.out.println(findTheDifference(a,b));
    }

    public static char findTheDifference(String s, String t) {
        char []ss= s.toCharArray();
        char []tt = t.toCharArray();

        Map<Character,Integer> maps = new HashMap<Character,Integer>();
        Map<Character,Integer> mapt = new HashMap<Character,Integer>();

        for(int i = 0 ; i < ss.length; i++){
            if(maps.containsKey(ss[i])){
                maps.put(ss[i],maps.get(ss[i]) + 1);
            }else{
                maps.put(ss[i],1);
            }
        }

        for(int i = 0 ; i < tt.length; i++){
            if(mapt.containsKey(tt[i])){
                mapt.put(tt[i],mapt.get(tt[i]) + 1);
            }else{
                mapt.put(tt[i],1);
            }
        }


        for(Character c : mapt.keySet()){
            if(mapt.get(c) != maps.get(c)){
                return c;
            }
        }



        return 'a';

    }
}
